  # Retourne le logo SVG d'une compétence
  def skill_logo(skill, size: 16)
    skill_name = skill.is_a?(Skill) ? skill.name : skill.to_s
    data = SKILL_LOGOS[skill_name]

    if data
      content_tag(:svg,
        data[:icon].html_safe,
        class: "skill-logo",
        width: size,
        height: size,
        viewBox: "0 0 24 24",
        fill: "currentColor",
        style: "color: #{data[:color]};",
        'aria-hidden': true
      )
    else
      # Icône par défaut (code)
      content_tag(:svg,
        '<path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4"/>'.html_safe,
        class: "skill-logo",
        width: size,
        height: size,
        viewBox: "0 0 24 24",
        fill: "none",
        stroke: "currentColor",
        style: "color: var(--muted-foreground);",
        'aria-hidden': true
      )
    end
  end

  # Retourne la couleur associée à une compétence
  def skill_color(skill)
    skill_name = skill.is_a?(Skill) ? skill.name : skill.to_s
    SKILL_LOGOS.dig(skill_name, :color) || 'var(--muted-foreground)'
  end

  # Retourne l'icône SVG d'une catégorie
  def category_icon(category, size: 20)
    icon = CATEGORY_ICONS[category] || CATEGORY_ICONS['Autre']

    content_tag(:svg,
      icon.html_safe,
      class: "category-icon",
      width: size,
      height: size,
      viewBox: "0 0 24 24",
      fill: "none",
      stroke: "currentColor",
      'aria-hidden': true
    )
  end

  # Vérifie si une compétence a un logo
  def skill_has_logo?(skill)
    skill_name = skill.is_a?(Skill) ? skill.name : skill.to_s
    SKILL_LOGOS.key?(skill_name)
  end
end
